import React from 'react';
import { Link } from 'react-router-dom';
import styles from './categorie.module.css';
import { Context } from '../../pages/MainPage/MainPage';

function Categorie() {
  const { scrollToRef } = React.useContext(Context)
  const [ActiveIndex, setActiveIndex] = React.useState(0)

  return (
    <div>
      <div className={styles.list} >
        <Link className={ActiveIndex === 0 ? styles.categoriesOne : ''} to={'/'} onClick={scrollToRef}>
          <h1>Пиццы</h1>
        </Link>

        <Link className={ActiveIndex === 0 ?  styles.categoriesOne : ''} to={'/'} onClick={scrollToRef}>
          <h1>Закуски</h1>
        </Link>

        <Link className={ActiveIndex === 0 ?  styles.categoriesOne : ''} to={'/'} onClick={scrollToRef}>
          <h1>Напитки</h1>
        </Link>

        <Link className={ActiveIndex === 0 ?  styles.categoriesOne : ''} to={'/'} onClick={scrollToRef}>
          <h1>Дессерты</h1>
        </Link>

        <Link className={ActiveIndex === 0 ? categoriesOne : ''} to={'/'} onClick={scrollToRef}>
          <h1>Акции</h1>
        </Link>
      </div>
    </div>
  );
}

export default Categorie;
