import React from 'react';
import axios from 'axios';
import styles from './Drinks.module.css'
import { useDispatch, useSelector } from 'react-redux';
import { setAddDrinks, setDrinksCards } from '../../../Redux/slices/DrinksCardSlice'

function DrinksCard({img, name, desc, price}) {
    const dispatch = useDispatch()
    const Drinks = useSelector((state) => state.DrinksCard.DrinksCards)

    React.useEffect(() => {
        axios
          .get('https://demo5518646.mockable.io/DrinksCard')
          .then((res) => {
            dispatch(setDrinksCards(res.data));
          });

    }, [dispatch, Drinks]);

    const onClickAdd = () => {
      const pushSnacks = {img, name, desc, price}
      dispatch(setAddDrinks(pushSnacks))
    }

    const search = useSelector((state) => state.filter.search)
    const DrinksCards = Drinks ? Drinks.filter(card => card.name.toLowerCase().includes(search.toLowerCase())) : [];
  return (
    <div>
      <h1 className={styles.MenuDrinks}>Все Напитки</h1>
        <div className={styles.inline}>
          {DrinksCards.map(card => {<h1></h1>})}
        </div>
    </div>
  )
}

export default DrinksCard
