import React from 'react';
import axios from 'axios';
import styles from '../css/Drinks.module.css'
import { useDispatch, useSelector } from 'react-redux';
import { setAddDrinks, setDrinksCards } from '../../../Redux/slices/DrinksCardSlice'

function DrinksCard({img, name, desc, price}) {
    const dispatch = useDispatch()
    const Drinks = useSelector(state => state.DrinksCard.DrinksCards)

    React.useEffect(() => {
        axios
          .get('https://demo5518646.mockable.io/DrinksCard')
          .then((res) => {
            dispatch(setDrinksCards(res.data));
          });
    }, [dispatch, Drinks]);

    const onClickAdd = () => {
      const pushDrinks = {img, name, desc, price}
      dispatch(setAddDrinks(pushDrinks))
    }

   
    return (
      <div>
        <h1 className={styles.MenuSnacks}>Все Закуски</h1>
        <div className={styles.inline}>
          {SnacksCard.map((card, index) => (

            <div className={styles.card} key={card.id}  >
              <img src={card.img} className={styles.imgPizza} alt="logo" />
              <div className={styles.PizzaName}>
                <h1>{card.name}</h1>
              </div>

              <div>
                <h4 className={styles.desc}>{card.desc}</h4>
              </div>

              <div className={card.specialCard ? '' : styles.fixBag}>
                <div className={styles.price}>
                  <h2>{card.price}</h2>
                </div>

                <button onClick={onClickAdd} className={styles.btn}> Добавить</button>

              </div>
            </div>
          ))}
        </div>
      </div>
  )
}

export default DrinksCard;
