import { createSlice } from "@reduxjs/toolkit";

export const SnackscardSlice = createSlice({
  name: "Snacks",
  initialState: {
    SnacksCards: [],
    AddSnacks: []
  },
  reducers: {
    setSnacksCards(state, action) {
      state.SnacksCards = action.payload;
    },
    setAddSnacks(state, push) {
     state.AddSnacks.push
    }
  },
});

export const { setSnacksCards } = SnackscardSlice.actions;
export default SnackscardSlice.reducer;
