// В компоненте TypeOption
function TypeOption({ type, id }) {
  const dispatch = useDispatch();
  const ActiveType = useSelector(state => state.Pizza.ActiveType);

  const handleTypeClick = () => {
    dispatch(setActiveType(id));
  };

  React.useEffect(() => {
    console.log(ActiveType);
  }, [ActiveType]);

  return (
    <div>
      {type.map((typeOption) => (
        <label key={typeOption} className={ActiveType === id ? styles.ActiveTradition : styles.radioType} 
               onClick={handleTypeClick}>
          <input className={styles.InputRadio} type="radio" value={typeOption} checked={ActiveType === id} />
          {typeOption}
        </label>
      ))}
    </div>
  );
}
