import React from 'react';
import Categorie from '../../components/Categories/categorie';
import PizzaCard from '../../components/PizzaCard/PizzaCard';
import SnacksCard from '../../components/SnacksCard/SnacksCard'
import OftenOrder from '../../components/OftenOrder/ListMiniCard';
import Footer from './Footer/Footer';
import { Route, Routes } from 'react-router-dom';

export const Context = React.createContext('')

function MainPage() {
  const [cards, setCards] = React.useState([]);
  const [OftOrdCard, setOftOrdCard] = React.useState([]);
  const [isLoading, setIsLoading] = React.useState(true);
  const [selectedSize, setSelectedSize] = React.useState('medium');



  return (
      <Context.Provider value={{ cards, setCards, OftOrdCard, setOftOrdCard, 
      AddBtn, addBtn, isLoading, setIsLoading, selectedSize, setSelectedSize}}>
            <OftenOrder />
            <Categorie />
            <Routes>
             <Route path='/' element={<PizzaCard/>} />            
             <Route path='/SnacksCard' element={<SnacksCard/>} />            
            </Routes>
            <Footer />
       </Context.Provider>
  );
}

export default MainPage;
