import React, { useState } from 'react';

export default function TestComp() {
  const [product, setProduct] = useState(0);
  const max = 10;
  const min = 0;

  const changeNumberMinus = () => {
    if (product > min) {
      setProduct(product - 1);
    }
  };

  const changeNumberPlus = () => {
    if (product < max) {
      setProduct(product + 1);
    }
  };

  return (
    <div>
      <h1>Количество продуктовфвфывф: {product}</h1>
      <button onClick={changeNumberPlus}>Добавить</button>
      <button onClick={changeNumberMinus}>Убрать</button>
    </div>
  );
}
