import React, { useContext, useState } from 'react';
import styles from './PizzaCard.module.css';
import { Context } from '../../../pages/MainPage/MainPage';
import CardSkeleton from './CardSkeleton/CardSkeleton';
import { useDispatch, useSelector } from 'react-redux';
import { setAddPizza } from '../../../Redux/slices/Pizza';
import axios from 'axios';


function PizzaCard({id, img, name, price}) {
  const { setPizzaCards, setIsLoading } = React.useContext(Context)

  React.useEffect(() => {
    axios
    .get('  https://demo5518646.mockable.io/PizzaCard  ')
    .then((res) => {
      setPizzaCards(res.data);
      setIsLoading();
    })
  }, [setPizzaCards, setIsLoading]);


  const dispatch = useDispatch()
  const onCLickAdd = () => {
    const pushCard = {
     id, name, img, price
    }
    dispatch(setAddPizza(pushCard))
  }

  const { PizzaCards, isLoading} = useContext(Context)

  const search = useSelector((state) => state.filter.search)

  const ChooseType = (index, type) => {
    const updatedState = [...activeTypeState];
    updatedState[index] = type;
    setActiveTypeState(updatedState);
  };
 
  const cardList = PizzaCards ? PizzaCards.filter(card => card.name.toLowerCase().includes(search.toLowerCase())) : [];
  const [activeTypeState, setActiveTypeState] = useState(cardList.map(() => 0)); 

  return (
    <div>
      <div >
        <h1 className={styles.MenuPizza}>Все Пиццы</h1>
        <div className={styles.inline}>
          {cardList.map((card, index) => isLoading ? <CardSkeleton key={index} /> : (

            <div className={`
            ${card.FixOne ? styles.FixOne : styles.card}
            ${card.FixTwo ? styles.FixTwo : styles.card}
            ${card.FixThree ? styles.FixThree : styles.card}
            ${card.FixFour ? styles.FixFour : styles.card}
            ${card.FixFive ? styles.FixFive : styles.card}`} key={card.id}  >
              <img src={card.img} className={styles.imgPizza} alt="logo" />
              <div className={card.specialCard ? styles.fixBag : styles.PizzaName}>
                <h1>{card.name}</h1>
              </div>

              <div className={styles.categories}>
                <ul className={styles.typeSelector} >
                  <li className={activeTypeState[index] === 0 ? styles.ActiveTradition : styles.tradition}
                    onClick={() => ChooseType(index, 0)}>
                    {card.tradition}
                  </li>

                  <li className={activeTypeState[index] === 1 ? styles.ActiveThin : styles.thin}
                    onClick={() => ChooseType(index, 1)}>
                    {card.thin}
                  </li>
                </ul>

                <ul className={styles.sizeSelector}>
                  <li className={styles.TwentyFiveSm}>
                    {card.sizes.small}
                  </li>

                  <li className={styles.ThirtySm}>
                    {card.sizes.medium}
                  </li>

                  <li className={styles.ThirtyFiveSm}>
                    {card.sizes.large}
                  </li>

                </ul>
              </div>

              <div className={styles.price}>
                <h2>{card.price}</h2>
              </div>

              <button onClick={onCLickAdd} className={styles.btn}>Добавить {}</button>

            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default PizzaCard;
