// Ваш новый компонент TypeOption.jsx

import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setAc } from '../../../Redux/slices/Pizza';
import styles from '../../../utils/css/PizzaCard.module.css';

function TypeOption({ typeOption, cardId }) {
  const dispatch = useDispatch();
  const ActiveType = useSelector(state => state.Pizza.ActiveType);

  const handleTypeClick = () => {
    dispatch(setActiveType({ cardId, ActiveType: typeOption }));
  };

  return (
    <label
      className={ActiveType === typeOption ? styles.ActiveTradition : styles.radioType}
      onClick={handleTypeClick}
    >
      <input
        className={styles.InputRadio}
        type="radio"
        value={typeOption}
        defaultChecked={ActiveType === typeOption}
      />
      {typeOption}
    </label>
  );
}

export default TypeOption;
