import React from 'react'
import styles from '../css/Drinks.module.css'
import { useDispatch, useSelector } from 'react-redux';
import { setAddDrinks } from '../../../Redux/slices/Drink'
import axios from 'axios';
import { setDrinksCards } from '../../../Redux/slices/Drink';


function Snacks({ img, name, desc, price, FixOne, FixTwo, FixThree,
  FixFour,
  FixFive,
  FixSix,
  FixSeven,
  FixEight,
  FixNine,
  FixTen,
  FixEleven,
  FixSoki,
  FixThirtheen,
  FixFourteen }) {
  const dispatch = useDispatch()
  const Drinks = useSelector(state => state.Drink.DrinksCards)

  React.useEffect(() => {
    axios
      .get('https://demo5518646.mockable.io/DrinksCard')
      .then((res) => {
        dispatch(setDrinksCards(res.data.card));
      })
  }, []);


  const onClickAdd = () => {
    const pushDrinks = { img, name, desc, price }
    dispatch(setAddDrinks(pushDrinks))
  }

  const search = useSelector((state) => state.filter.search)

  const DrinksCard = Drinks
    ? Drinks.filter(card => card.name.toLowerCase().includes(search.toLowerCase()))
    : [];

  const FixClass = [
    FixOne ? styles.FixOne : styles.card,
    FixTwo ? styles.FixTwo : styles.card,
  FixThree ? styles.FixThree : styles.card,
  FixFour ? styles.FixFour : styles.card,
  FixFive ? styles.FixFive : styles.card,
  FixSix ? styles.FixSix : styles.card,
  FixSeven ? styles.FixSeven : styles.card,
  FixEight ? styles.FixEight : styles.card,
  FixNine ? styles.FixNine : styles.card,
  FixTen ? styles.FixTen : styles.card,
  FixEleven ? styles.FixEleven : styles.card,
  FixSoki ? styles.FixSoki : styles.card,
  FixThirtheen ? styles.FixThirtheen : styles.card,
  FixFourteen ? styles.FixFourteen : styles.card
  ]

  return (
    <div>
      <h1 className={styles.MenuDrinks}>Все Закуски</h1>
      <div className={styles.inline}>
        {DrinksCard.map((card) => (

          <div className={FixClass.join(' ')} key={card.id}  >
            <img src={card.img} className={styles.imgDrink} alt="logo" />
            <div className={styles.DrinkName}>
              <h1>{card.name}</h1>
            </div>

            <div>
              <h4 className={styles.desc}>{card.desc}</h4>
            </div>

            <div>
              <div className={styles.price}>
                <h2>{card.price}</h2>
              </div>

              <button onClick={onClickAdd} className={styles.btn}> Добавить</button>

            </div>
          </div>
        ))}
      </div>
    </div>
  )
}

export default Snacks
