import React, { useContext, useState } from 'react';
import styles from './cards.module.css';
import { Context } from '../../pages/MainPage/MainPage';
import CardSkeleton from './CardSkeleton/CardSkeleton';
import { useDispatch, useSelector } from 'react-redux';
import { addItem } from '../../Redux/slices/cardSlice';


function Cards({id, img, name, price}) {
  const dispatch = useDispatch()
  const onCLickAdd = () => {
    const pushCard = {
     id, name, img, price
    }
    dispatch(addItem(pushCard))
  }
  const { cards, isLoading } = useContext(Context)

  const [ActiveType, setActiveType] = useState(0)

  const search = useSelector((state) => state.filter.search)

  const ChooseType = (index, type) => {
    const updatedState = [...activeTypeState];
    updatedState[index] = type;
    setActiveTypeState(updatedState);
  };
 
  const cardList = cards ? cards.filter(card => card.name.toLowerCase().includes(search.toLowerCase())) : [];

  return (
    <div>
      <div >
        <h1 className={styles.MenuPizza}>Все Пиццы</h1>
        <div className={styles.inline}>
          {cardList.map((card, index) => isLoading ? <CardSkeleton key={index} /> : (

            <div className={styles.card} key={card.id}  >
              <img src={card.img} className={styles.imgPizza} alt="logo" />
              <div className={card.specialCard ? styles.fixBag : styles.PizzaName}>
                <h1>{card.name}</h1>
              </div>

              <div className={styles.categories}>
                <ul className={styles.typeSelector} >
                  <li className={ActiveType === 0 ? styles.ActiveTradition : styles.tradition}
                    onClick={() => ChooseType(0)}>
                    {card.tradition}
                  </li>

                  <li className={ActiveType === 1 ? styles.ActiveThin : styles.thin}
                    onClick={() => ChooseType(1)}>
                    {card.thin}
                  </li>
                </ul>

                <ul className={styles.sizeSelector}>
                  <li className={styles.TwentyFiveSm}>
                    {card.sizes.small}
                  </li>

                  <li className={styles.ThirtySm}>
                    {card.sizes.medium}
                  </li>

                  <li className={styles.ThirtyFiveSm}>
                    {card.sizes.large}
                  </li>

                </ul>
              </div>

              <div className={styles.price}>
                <h2>{card.price}</h2>
              </div>

              <button onClick={onCLickAdd} className={styles.btn}>Добавить {}</button>

            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Cards;
