import { createSlice } from "@reduxjs/toolkit";

export const filterSlice = createSlice({
name: 'filter',
initialState: { 
    ActiveIndex: 0
},
reducers: {
    setActiveIndex: (state, action) => {
    state.ActiveIndex = action.payload
    }
}
})

ц
export default filterSlice.reducer
