import React, { useState } from 'react'
import Text from './Text'

function ShowText() {
  const [change, setChange] = useState(false)
  const Click = () => {setChange(!change)}
  return (
    <div>
      {change && <Text /> }
      <button onClick={Click}>
         {change ? 'Hide' : 'Show'}
      </button>
      
    </div>
  )
}


export default ShowText
