import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import styles from './categorie.module.css';
import list from './list.json';

function Categorie() {
  useEffect(() => {
    // Получаем ссылку на элемент, на который нужно прокрутить
    const targetRef = scrollToRef.current;
    if (targetRef) {
      targetRef.scrollIntoView({ behavior: 'smooth' });
    }
  }, []);

  // Создаем ссылку на элемент, к которому нужно прокрутить
  const scrollToRef = React.useRef(null);

  const categories = list.map((obj) => (
    <h1 className={styles.list} key={obj.id}>
      <Link
        className={styles.categories}
        to={obj.path}
        onClick={() => (scrollToRef.current = targetRef)}
      >
        {obj.name}
      </Link>
    </h1>
  ));

  return (
    <div>
      {categories}
      <div ref={scrollToRef}></div> {/* Элемент, к которому нужно прокрутить */}
    </div>
  );
}

export default Categorie;
