import React, { useContext } from 'react';
import styles from './cards.module.css';
import { Context } from '../../pages/MainPage/MainPage';
import CardSkeleton from './CardSkeleton/CardSkeleton';

function Cards() {

  const { search, cards, AddBtn, addBtn, isLoading, setCards } = useContext(Context);

  const handleSizeChange = (index, size) => {
    const updatedCards = [...cards];
    updatedCards[index].selectedSize = size;
    setCards(updatedCards);
  };

  const cardList = cards ? cards.filter(card => card.name.toLowerCase().includes(search.toLowerCase())) : [];

  return (
    <div>
      <h1 className={styles.MenuPizza}>Все Пиццы</h1>
      <div className={styles.inline}>
        {cardList.map((card, i) => isLoading ? <CardSkeleton key={i} /> : (

          <div className={styles.card} key={card.id}  >
            <img src={card.img} className={styles.imgPizza} alt="logo" />

            {/* ... (other card content) ... */}

            <div className={styles.categories}>
              <div className={styles.typeSelector}>
                <label className={card.selectedSize === 'tradition' ? styles.ActiveTradition : styles.tradition} onClick={() => handleSizeChange(i, 'tradition')}>
                  <input type="radio" value="large" checked={card.selectedSize === 'tradition'} readOnly />
                  {card.tradition}
                </label>

                <label className={card.selectedSize === 'thin' ? styles.ActiveThin : styles.thin} onClick={() => handleSizeChange(i, 'thin')}>
                  <input type="radio" value="large" checked={card.selectedSize === 'thin'} readOnly />
                  {card.thin}
                </label>
              </div>

              {/* ... (other size options) ... */}
            </div>

            {/* ... (other card content) ... */}

          </div>
        ))}
      </div>
    </div>
  );
}

export default Cards;
