import React, { useContext, useState } from 'react';
import styles from './cards.module.css';
import { Context } from '../../pages/MainPage/MainPage';
import CardSkeleton from './CardSkeleton/CardSkeleton';

function Cards() {

  const { search, cards, AddBtn, addBtn, isLoading, setCards } = useContext(Context)

  const handleSizeChange = (index, size) => {
    const updatedCards = [...cards];
    updatedCards[index].selectedSize = size;
    setCards(updatedCards);
  };

  

  const [ActiveIndex, setActiveIndex] = useState(0)

  const cardList = cards ? cards.filter(card => card.name.toLowerCase().includes(search.toLowerCase())) : [];

  return (
    <div>
      <h1 className={styles.MenuPizza}>Все Пиццы</h1>
      <div className={styles.inline}>
        {cardList.map((card, i) => isLoading ? <CardSkeleton key={i} /> : (

          <div className={styles.card} key={card.id}  >
            <img src={card.img} className={styles.imgPizza} alt="logo" />

            <div className={card.specialCard ? styles.fixBag : styles.PizzaName}>
              <h1>{card.name}</h1>
            </div>

            <div className={styles.categories}>
              <div className={styles.typeSelector} >
                <label className={ActiveIndex === 0 ? styles.ActiveTradition : styles.tradition} 
                onChange={()=> clickCategories(i, 'tradition')}>
                  <input type="radio" value="large" checked={card.selectedSize === 'tradition'}
                    onChange={() =>  handleSizeChange(i, 'tradition')}
                  />
                  {card.tradition}
                </label>

                <label className={ActiveIndex === 1 ? styles.ActiveThin : styles.thin} 
                onChange={()=> clickCategories(i, 'thin')}>
                  <input type="radio" value="large" checked={card.selectedSize === 'thin'}
                    onChange={() => { handleSizeChange(i, 'thin')}}
                  />
                  {card.thin}
                </label>
              </div>

              <div className={styles.sizeSelector}>
                <label className={styles.TwentyFiveSm}>
                  <input type="radio" value="small" checked={card.selectedSize === 'small'}
                    onChange={() => handleSizeChange(i, 'small')} />
                  {card.sizes.small}
                </label>

                <label className={styles.ThirtySm}>
                  <input type="radio" value="medium" checked={card.selectedSize === 'medium'}
                    onChange={() => handleSizeChange(i, 'medium')} />
                  {card.sizes.medium}
                </label>

                <label className={styles.ThirtyFiveSm}>
                  <input type="radio" value="large" checked={card.selectedSize === 'large'}
                    onChange={() => handleSizeChange(i, 'large')} />
                  {card.sizes.large}
                </label>

              </div>
            </div>

            <div className={styles.price}>
              <h2>{card.price}</h2>
            </div>

            <button onClick={AddBtn} className={styles.btn}>Добавить {addBtn}</button>

          </div>
        ))}
      </div>
    </div>
  );
}

export default Cards;
