import React, { Component } from "react";
import "../App.css";

class testcomp extends Component {
  
 constructor(props) {
   super(props)
   this.Ref = null
   this.setRef = (element) => {
      this.Ref = element
  }
 }

 componentDidMount(){
  if(this.Ref){
  this.Ref.focus()}
}
 
  
  render() {  
    return(
    <div>
     <input type="text" ref={this.setRef}></input>
    </div>)
  }
}

export default testcomp;
