import React, { useContext } from 'react'
import styles from '../css/Drinks.module.css'
import axios from 'axios';
import { Context } from '../../../pages/MainPage/MainPage';
import { useSelector } from 'react-redux';


function Snacks({ img, name, desc, price }) {
const {DrinksCards, setDrinksCards} = useContext(Context)
 
 React.useEffect(() => {
    if (Snacks.length === 0) {
      axios
        .get('https://demo5518646.mockable.io/DrinksCard')
        .then((res) => {
          setDrinksCards(res.data);
        });
    }
  }, []);


  const onClickAdd = () => {
    const pushSnacks = {img, name, desc, price}
    dispatch(setAddDrinks(pushSnacks))
  }
  const search = useSelector((state) => state.filter.search)

  const SnacksCard = Snacks ? Snacks.filter(card => card.name.toLowerCase().includes(search.toLowerCase())) : [];

  return (
      <div>
        <h1 className={styles.MenuDrinks}>Все Напитки</h1>
        <div className={styles.inline}>
          {SnacksCard.map((card, index) => (

            <div className={styles.card} key={card.id}  >
              <img src={card.img} className={styles.imgPizza} alt="logo" />
              <div className={styles.PizzaName}>
                <h1>{card.name}</h1>
              </div>

              <div>
                <h4 className={styles.desc}>{card.desc}</h4>
              </div>

              <div className={card.specialCard ? '' : styles.fixBag}>
                <div className={styles.price}>
                  <h2>{card.price}</h2>
                </div>

                <button onClick={onClickAdd} className={styles.btn}> Добавить</button>

              </div>
            </div>
          ))}
        </div>
      </div>
  )
}

export default Snacks
