import React from 'react';
import Cards from './cards';
import { Context } from '../../pages/MainPage/MainPage';

function PizzaCard() {
  const { setCards, setIsLoading } = React.useContext(Context)

  React.useEffect(() => {
    fetch('https://demo5518646.mockable.io/PizzaCard')
      .then((response) => response.json())
      .then((data) => {
         const cardsChooseSize = data.map((card) => ({ ...card, selectedSize: 'medium' }));
        setCards(cardsChooseSize);
        setTimeout(() => {
          setIsLoading();
        }, 500);
      })
  }, []);

  return (
    <div>
      <Cards />
    </div>
  )
}

export default PizzaCard
