import React, { useRef } from 'react';
import { Link } from 'react-router-dom';
import styles from './categorie.module.css';
import list from './list.json';

function Categorie() {
  const targetRef = useRef(null); // Create a reference to the element to scroll to

  const scrollToRef = () => {
    targetRef.current.scrollIntoView({ behavior: 'smooth' });
  };

  const categories = list.map((obj) => (
    <h1 className={styles.list} key={obj.id}>
      <Link
        className={styles.categories}
        to={obj.path}
        onClick={scrollToRef} // Call the scroll function on click
      >
        {obj.name}
      </Link>
    </h1>
  ));

  return (
    <div>
      {categories}
      <div ref={targetRef}></div> {/* Element to which we want to scroll */}
    </div>
  );
}

export default Categorie;
