import React, { Component } from "react";

class main extends Component {
  constructor(props) {
    super(props);

    this.state = {
      number: 0,
    };
  }

  click = () => {
    this.setState((prevState) => {
      return { number: prevState.number + 1 };
    })
  }

  render() {
    let { number } = this.state;
    return (
      <div>
        <h1>Number = {number}</h1>
        <button onClick={this.click}></button>
      </div>
    );
  }
}

export default main;
