import React, { Component } from "react";

class Shetchik extends Component {
  constructor(props) {
    super(props);

    this.state = {
      plus: 0,
      minus
    };
  }

  onclick() {
    this.setState({
      plus: this.state.plus + 1,
    });
  }
  ofclick() {
    this.setState({
      minus: this.state.minus - 1,
    });
  }

  render() {
    return (
      <div>
        <h1>
          <strong>Number = {this.state.num}</strong>
        </h1>
        <button onClick={() => this.onclick()}>+</button>
      </div>
    );
  }
}

export default Shetchik;
