import { createSlice } from "@reduxjs/toolkit";

export const SnackscardSlice = createSlice({
  name: "Snacks",
  initialState: {
    DrinksCards: [],
    AddSnacks: []
  },
  reducers: {
    setDrinksCards(state, action) {
      state.SnacksCards = action.payload;
    },
    setAddDrinks(state, action) {
     state.AddSnacks.push(action.payload) 
    }
  },
});

export const { setDrinksCards, setAddSnacks } = SnackscardSlice.actions;
export default SnackscardSlice.reducer;
