import React from 'react'
import styles from '../css/'
import { useDispatch, useSelector } from 'react-redux';
import { setAddPizza } from '../../../Redux/slices/Pizza'
import axios from 'axios';
import { setPizzaCard } from '../../../Redux/slices/Pizza';

function PizzaCard({ img, name, desc, price }) {
  const dispatch = useDispatch()
  const Pizza = useSelector(state => state.Pizza.PizzaCards)

  React.useEffect(() => {
    axios
      .get('http://demo5518646.mockable.io/PizzaCard')
      .then((res) => {
        dispatch(setPizzaCard(res.data.card));
      })
  }, );


  const onClickAdd = () => {
    const pushDrinks = { img, name, desc, price }
    dispatch(setAddPizza(pushDrinks))
  }

  const search = useSelector((state) => state.filter.search)

  const PizzaCard = Pizza
    ? Pizza.filter(card => card.name.toLowerCase().includes(search.toLowerCase()))
    : [];

  return (
    <div>
      <h1 className={styles.MenuPizza}>Все Напитки</h1>
      <div className={styles.inline}>
        {PizzaCard.map((card) => (

          <div className={styles.card} key={card.id}  >
            <img src={card.img} className={styles.imgPizza} alt="logo" />
            <div className={styles.PizzaName}>
              <h1>{card.name}</h1>
            </div>

            <div>
              <h4 className={styles.desc}>{card.desc}</h4>
            </div>

            <div>
              <div className={styles.price}>
                <h2>{card.price}</h2>
              </div>

              <button onClick={onClickAdd} className={styles.btn}> Добавить</button>

            </div>
          </div>
        ))}
      </div>
    </div>
  )
}

export default PizzaCard
