import React from 'react'
import styles from '../css/Snacks.module.css'
import { useDispatch, useSelector } from 'react-redux';
import { setAddDrinks } from '../../../Redux/slices/Drink'
import axios from 'axios';
import { setDrinksCards } from '../../../Redux/slices/Drink';


function Snacks({ img, name, desc, price }) {
  const dispatch = useDispatch()
  const Drinks = useSelector(state => state.Drink.DrinksCards)
 
 React.useEffect(() => {
  axios
  .get('https://demo5518646.mockable.io/DrinksCard')
  .then((res) => {
    const data = res.data.card; // Извлекаем массив данных
    dispatch(setDrinksCards(data));
  })
  .catch((error) => {
    console.error("Ошибка при получении данных:", error);
  });
  }, []);


  const onClickAdd = () => {
    const pushSnacks = {img, name, desc, price}
    dispatch(setAddDrinks(pushSnacks))
  }
  const search = useSelector((state) => state.filter.search)

  const DrinksCard = Drinks 
  ? Drinks.filter(card => card.name.toLowerCase().includes(search.toLowerCase())) 
  : [];

  return (
      <div>
        <h1 className={styles.MenuSnacks}>Все Закуски</h1>
        <div className={styles.inline}>
          {DrinksCard.map((card, index) => (

            <div className={styles.card} key={card.id}  >
              <img src={card.img} className={styles.imgPizza} alt="logo" />
              <div className={styles.PizzaName}>
                <h1>{card.name}</h1>
              </div>

              <div>
                <h4 className={styles.desc}>{card.desc}</h4>
              </div>

              <div className={card.specialCard ? '' : styles.fixBag}>
                <div className={styles.price}>
                  <h2>{card.price}</h2>
                </div>

                <button onClick={onClickAdd} className={styles.btn}> Добавить</button>

              </div>
            </div>
          ))}
        </div>
      </div>
  )
}

export default Snacks
