import React, { Component } from "react";
import Children from "./children";

export class Parent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      JustParents: "Разрешить сайту",
    };
    this.greetParent = this.greetParent.bind(this)
  }

  greetParent(nameChild) {
    window.confirm(`Hello ${this.state.JustParents} from ${nameChild}`)
  }

  render() {
    return (
      <div>
        <Children svoistvo={this.greetParent}></Children>
      </div>
    );
  }
}

export default Parent;
