import React, { Component } from 'react'

class main extends Component {
  constructor(props) {
    super(props)
    this.Ref = React.createRef()  
  }
   componentDidMount(){
   if(this.Ref){
   this.Ref.current.focus()}}
  

  render() {
    return (
      <div>
        <input ref={this.Ref}></input>
      <button></button>     
      </div>
    )
  }
}

export default main
