class State extends Component {
  constructor() {
    super();
    this.state = {
      message: "Hello!"
    };
  }

 changeMessage = () => {
  this.setState = {
  message: "Goodbye"}
 }

  render() {
    return (
    <div>
        <h1>{this.state.message}</h1>
        <button onClick={this.changeMessage}>Subscribe</button>
      </div>
    );
  }
}

export default State;
