import React from 'react';
import { Link } from 'react-router-dom';
import styles from './categorie.module.css';
import { Context } from '../../pages/MainPage/MainPage';

function Categorie() {
  const { scrollToRef } = React.useContext(Context)
  const [ActiveIndex, setActiveIndex] = React.useState(2)

  return (
      <div className={styles.list} >
        <Link className={ActiveIndex === 0 ? styles.categoriesOne : ''} to={'/'} onClick={scrollToRef}>
          <h1 className={styles.textOne}>Пиццы</h1 >
        </Link>

        <Link className={ActiveIndex === 1 ?  styles.categoriesTwo : ''} to={'/'} onClick={scrollToRef}>
          <h1 className={styles.textTwo}>Закуски</h1>
        </Link>

        <Link className={ActiveIndex === 2 ?  styles.categoriesThree : ''} to={'/'} onClick={scrollToRef}>
          <h1 className={styles.textThree}>Напитки</h1 >
        </Link>

        <Link className={ActiveIndex === 3 ?  styles.categoriesFour : ''} to={'/'} onClick={scrollToRef}>
          <h1 className={styles.textFour}>Дессерты</h1 >
        </Link>

        <Link className={ActiveIndex === 4 ?  styles.categoriesFive: ''} to={'/'} onClick={scrollToRef}>
          <h1 className={styles.textFive}>Акции</h1>
        </Link>
      </div>
  );
}

export default Categorie;
