import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setActiveType } from '../Redux/slices/Pizza';
import styles from './css/TypeOption.module.css';

function TypeOption({ type }) {
  const dispatch = useDispatch();
  const ActiveType = useSelector(state => state.Pizza.ActiveType);

  const handleTypeClick = (event, index) => {
    dispatch(setActiveType([...ActiveType.map((data, i) => i === index ? event.target.value : data)]));
  };
  

  
  return (
    <div>
      {type.map((typeOption, i) => (
      <label
        className={ActiveType === (typeOption) ? styles.ActiveTradition : styles.radioType} 
        onClick={(e) => handleTypeClick(e, i)}>
        <input className={styles.InputRadio} type="radio" value={typeOption} defaultChecked={ActiveType === String(typeOption)}/>
        {typeOption}
      </label>
      ))}
    </div>
  );
}

export default TypeOption;
