import React from 'react';
import { Link } from 'react-router-dom';
import styles from './categorie.module.css';
import list from './list.json';
import { Context } from '../../pages/MainPage/MainPage';

function Categorie() {
 const { scrollToRef } = React.useContext(Context)
 
  const categories = list.map((obj) => (
    <h1 className={styles.list} key={obj.id}>
      <Link
        className={styles.categories} to={obj.path} onClick={scrollToRef}>
        {obj.name}
      </Link>
    </h1>
  ));

  return (
    <div>
      {categories}
    </div>
  );
}

export default Categorie;
