import React, { useContext } from 'react';
import styles from './cards.module.css';
import { Context } from '../../pages/MainPage/MainPage';

function Cards(props) {
  const {cards, setCards} = useContext(Context)
  const {search} = React.useContext(Context)
  const { targetRef } = props
  
  const cardList = cards.filter(card => card.name.toLowerCase().includes(search.toLowerCase()))

  return (
    <div ref={targetRef}>
      <h1 className={styles.MenuPizza}> Все Пиццы</h1>
      <div className={styles.inline}>
        {cardList.map((card) => (
          <div className={styles.card} key={card.id}  >
            <img src={card.img} className={styles.pizza} alt="logo" />
            <div className={styles.description}>
              <h1>{card.name}</h1>
              <h4>{card.desc}</h4>
              <h2>{card.price}</h2>
            </div>
              <button className={styles.btn}>Добавить</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Cards;
