import React, { useState } from "react";
import Text from "./Text";

function ShowText() {
  const [change, setChange] = useState(false);
  const Click = () => {
    setChange(!change);
  };
  return (
    <div>
      <button onClick={Click}> {change ? "Hide" : "Show"}</button>
      {change && <Text />}
    </div>
  );
}

export default ShowText;
