    const type = ['Традиционое', 'Тонкое']
  return (
    
      <div className={styles.categories}>
              <ul className={styles.typeSelector} >
                <li className={ActiveType === 0 ? styles.ActiveTradition : styles.tradition} 
                onClick={()=> ChooseType(0)}>
                  {card.tradition}
                </li>

                <li className={ActiveType === 1 ? styles.ActiveThin : styles.thin} 
                onClick={()=> ChooseType(1)}>
                  {card.thin}
                </li>
              </ul>

              <ul className={styles.sizeSelector}>
                <li className={styles.TwentyFiveSm}>                   
                  {card.sizes.small}
                </li>

                <li className={styles.ThirtySm}>
                  {card.sizes.medium}
                </li>

                <li className={styles.ThirtyFiveSm}>
                  {card.sizes.large}
                </li>

              </ul>
            </div>
  )
}

export default ChooseSize
