import Testcomp from "./testcomp";

class Main extends Component {

  constructor(props) {
    super(props)
    this.RefCLick = React.createRef()
  }
  
  clickhandler = () => {
  this.RefCLick.current.focus()}

  render() {
    return (
      <div>
       <Testcomp ref={this.RefCLick}></Testcomp>
       <button onClick={this.clickhandler}>Click Focus</button>
      </div>
    );
  }
}

export default Main;
