import { createSlice } from "@reduxjs/toolkit";

export const DrinkscardSlice = createSlice({
  name: "Drinks",
  initialState: {
    DrinkCards: [],
    AddSnacks: []
  },
  reducers: {
    setDrinkCards(state, action) {
      state.SnacksCards = action.payload;
    },
    setAddDrink(state, action) {
     state.AddSnacks.push(action.payload) 
    }
  },
});

export const {  setDrinkCards, setAddDrink } = DrinkscardSlice.actions;
export default DrinkscardSlice.reducer;
