import React from 'react';
import Header from '../Header/Header';
import Categorie from '../../components/Categories/categorie';
import ListCard from '../../components/PizzaCard/listCard';
import OftenOrder from '../../components/OftenOrder/ListMiniCard';
import Footer from '../Footer/Footer';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';

export const Context = React.createContext('')

function MainPage() {

  const [search, setSearch] = React.useState('');

  const targetRef = React.useRef(null); 

  const scrollToRef = () => {
    targetRef.current.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <Context.Provider value={{search, setSearch}}>
      <Router>
        <Header search={search} setSearch={setSearch} />
        <Categorie scrollToRef={scrollToRef}/>
        <OftenOrder />
        <Routes>
           <Route path="/" element={<ListCard search={search}  targetRef={targetRef}  />} />
        </Routes>
        <Footer />
      </Router>
    </Context.Provider>
  );
}

export default MainPage;
