import React from 'react';
import { Link } from 'react-router-dom';
import '../Categories/Caategorie.css'
import { Context } from '../../pages/MainPage/MainPage';

function Categorie() {
  const { scrollToRef } = React.useContext(Context)
  const [ActiveIndex, setActiveIndex] = React.useState(0)

  return (
      <div className='list' >
       <div className=''>
          <Link className={ActiveIndex === 0 ? 'categoriesOne' : ''} to={'/'} onClick={scrollToRef}>
            <h1 className={'textOne'}>Пиццы</h1 >
          </Link>
  
          <Link className={ActiveIndex === 1 ?  'categoriesTwo' : ''} to={'/'} onClick={scrollToRef}>
            <h1 className={'textTwo'}>Закуски</h1>
          </Link>
  
          <Link className={ActiveIndex === 2 ?  'categoriesThree' : ''} to={'/'} onClick={scrollToRef}>
            <h1 className={'textThree'}>Напитки</h1 >
          </Link>
  
          <Link className={ActiveIndex === 3 ?  'categoriesFour' : ''} to={'/'} onClick={scrollToRef}>
            <h1 className={'textFour'}>Дессерты</h1 >
          </Link>
  
          <Link className={ActiveIndex === 4 ?  'categoriesFive' : ''} to={'/'} onClick={scrollToRef}>
            <h1 className={'textFive'}>Акции</h1>
          </Link>
       </div>
      </div>
  );
}

export default Categorie;
