import axios from 'axios';
import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Snacks from './Snacks';
import { setSnacksCards } from '../../Redux/slices/SnacksCardSlice';

function SnacksCard() {
  const dispatch = useDispatch();
  const selectedCategory = useSelector(state => state.snacksCard.selectedCategory);

  useEffect(() => {
    // При загрузке страницы, проверьте, есть ли сохраненное состояние категории
    // Если есть, используйте его для запроса данных
    axios
      .get(`https://demo5518646.mockable.io/${selectedCategory}`)
      .then((res) => { dispatch(setSnacksCards(res.data)) });
  }, [dispatch, selectedCategory]); // При изменении selectedCategory будет выполнен запрос

  return <Snacks />;
}

export default SnacksCard;
