import React, { Component } from "react";
// import Main from "./components/main";
import './App.css'

class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
       number: 0
    }
  }

  click = () => {
  this.setState(prevState => ({number: this.prevState}))}

  render() {
    const {number} = this.state
    return (
      <div className="App">
       <button onClick={this.click}>Hello {number}</button>
      </div>
    );
  }
}

export default App;
