import React from 'react';
import Categorie from '../../components/Categories/categorie';
import ListCard from '../../components/PizzaCard/listCard';
import OftenOrder from '../../components/OftenOrder/ListMiniCard';
import Footer from './Footer/Footer';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';

export const Context = React.createContext('')

function MainPage() {
  
  return (
    <Context.Provider value={{ search, setSearch, targetRef, cards, setCards, OftOrdCard, setOftOrdCard, AddBtn, addBtn, scrollToRef, isLoading, setIsLoading}}>
      <Router>
        <OftenOrder />
        <Categorie />
        <Routes>
          <Route path="/" element={<ListCard />} />
        </Routes>
        <Footer />
      </Router>
    </Context.Provider>
  );
}

export default MainPage;
