import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setActiveType } from '../Redux/slices/Pizza';
import styles from './css/TypeOption.module.css';

function TypeOption({ typeOption, cardId }) {
  const dispatch = useDispatch();
  const ActiveType = useSelector(state => state.Pizza.ActiveType);

  const handleTypeClick = (event, index) => {
    dispatch(setActiveType([...ActiveType.map((data,i) => i ===index ? event.target.value:data )]));
  };

  return (
    <label
      className={ActiveType === String(typeOption) ? styles.ActiveTradition : styles.radioType}
      onClick={handleTypeClick}
    >
      <input
        className={styles.InputRadio}
        type="radio"
        value={typeOption}
        defaultChecked={ActiveType === String(typeOption)}
      />
      {typeOption}
    </label>
  );
}

export default TypeOption;
