import React, { Component } from "react";
import Children from "./children";

export class Parent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      JustParents: "Разрешить сайту",
    };

    this.greetParent = this.greetParent.bind(this);
  }

  greetParent(nameChild) {
    let confirmed = window.confirm(`${this.state.JustParents} ${nameChild}`);
    if (confirmed) {
      alert("Не удалось скачать файл. Иди тренируйся");
    } else {
      alert("Не удалось скачать файл. Иди тренируйся");
    }
  }

  render() {
    return (
      <div>
        <Children svoistvo={this.greetParent} />
      </div>
    );
  }
}

export default Parent;
