import React from 'react';
import styles from '../css/PizzaCard.module.css';
import CardSkeleton from './CardSkeleton/CardSkeleton';
import { useDispatch, useSelector } from 'react-redux';
import axios from 'axios';
import { setAddPizza, setIsLoading, setPizzaCard } from '../../../Redux/slices/Pizza';


function PizzaCard({id, img, name, price}) {
  const dispatch = useDispatch() 
  const PizzaCards = useSelector(state => state.Pizza.PizzaCards)
  React.useEffect(() => {
    axios
    .get('  https://demo5518646.mockable.io/PizzaCard  ')
    .then((res) => {
      dispatch(setPizzaCard(res.data.card))
      dispatch(setIsLoading())
    })
    const lastActiveIndex = localStorage.getItem('lastActiveIndex');
    if (lastActiveIndex !== null) {
      setActiveIndex(parseInt(lastActiveIndex));
    }
  }, []);


  const onCLickAdd = () => {
    const pushCard = {
     id, name, img, price
    }
    dispatch(setAddPizza(pushCard))
  }

  const search = useSelector((state) => state.filter.search)

  const [activeTypeState, setActiveIndex] = React.useState(0);
  const onCLickCategory = (index) => {
    setActiveIndex(index);
    localStorage.setItem('lastActiveIndex', index.toString());
  }

   const cardList = PizzaCards ? PizzaCards.filter(card => card.name.toLowerCase().includes(search.toLowerCase())) : [];
 
  const isLoading = useSelector(state => state.Pizza.isLoading)
 const typeNames = ["Традиционное", "Тонкое"]

  return (
    <div>
      <div >
        <h1 className={styles.MenuPizza}>Все Пиццы</h1>
        <div className={styles.inline}>
          {cardList.map((card, index) => isLoading ? <CardSkeleton key={index} /> : (

            <div className={`
            ${card.FixOne ? styles.FixOne : styles.card}
            ${card.FixTwo ? styles.FixTwo : styles.card}
            ${card.FixThree ? styles.FixThree : styles.card}
            ${card.FixFour ? styles.FixFour : styles.card}
            ${card.FixFive ? styles.FixFive : styles.card}
            ${card.FixSix ? styles.FixSix : styles.card}
            
            `} key={card.id}  >
              <img src={card.img} className={styles.imgPizza} alt="logo" />
              <div className={card.specialCard ? styles.fixBag : styles.PizzaName}>
                <h1>{card.name}</h1>
              </div>

              <div className={styles.categories}>
               <ul className={styles.typeSelector} >
                  <li className={activeTypeState === 0 ? styles.ActiveTradition : styles.ActiveThin}
                    onClick={() => onCLickCategory(index, 0)}>
                    {typeNames[card.tradition]}
                  </li>
                </ul>
                <ul className={styles.sizeSelector}>
                  <li className={styles.TwentyFiveSm}>
                    {card.sizes.small}
                  </li>

                  <li className={styles.ThirtySm}>
                    {card.sizes.medium}
                  </li>

                  <li className={styles.ThirtyFiveSm}>
                    {card.sizes.large}
                  </li>

                </ul>
              </div>

              <div className={styles.price}>
                <h2>{card.price}</h2>
              </div>

              <button onClick={onCLickAdd} className={styles.btn}>Добавить {}</button>

            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default PizzaCard;
