import React from 'react';
import { Link } from 'react-router-dom';
import styles from './categorie.module.css';
import list from './list.json';

function Categorie() {
  const categories = list.map((obj) => {
    const scrollToRef = (ref) => {
      ref.current.scrollIntoView({ behavior: 'smooth' });
    };

    // Создаем ссылку на элемент, к которому нужно прокрутить
    const targetRef = React.useRef(null);

    return (
      <h1 className={styles.list} key={obj.id}>
        <Link className={styles.categories} to={obj.path} onClick={() => scrollToRef(targetRef)}>
          {obj.name}
        </Link>
        <div ref={targetRef}></div> {/* Элемент, к которому нужно прокрутить */}
      </h1>
    );
  });

  return <div>{categories}</div>;
}

export default Categorie;
