import React from 'react';

function Utils() {
    const AllFunction = () => {
        const [cards, setCards] = React.useState([]);
        const [search, setSearch] = React.useState('');
        const [OftOrdCard, setOftOrdCard] = React.useState([]);

        const [addBtn, setAddBtn] = React.useState(1);
        const AddBtn = () => { setAddBtn(addBtn + 1) }

        const targetRef = React.useRef(null);
        const scrollToRef = () => {
            targetRef.current.scrollIntoView({ behavior: 'smooth' });
        };

        // Возвращаемые значения
        return {
            cards,
            setCards,
            search,
            setSearch,
            OftOrdCard,
            setOftOrdCard,
            addBtn,
            AddBtn,
            targetRef,
            scrollToRef
        };
    };

    // Используем функцию AllFunction и возвращаемые значения
    const functions = AllFunction();

    // Экспортируем возвращаемые значения
    return (
        <div>
            {functions.cards} {/* Пример использования */}
        </div>
    );
}

export default Utils;
