import React, { useContext } from 'react';
import Cards from './cards';
import { Context } from '../../pages/MainPage/MainPage';


function ListCard() {
  const {targetRef} = useContext(Context)  
  const {cards, setCards} = useContext(Context)  

  React.useEffect(() => {
    fetch('https://64c970ddb2980cec85c23f10.mockapi.io/PizzaCard')
      .then((response) => response.json())
      .then((data) => setCards(data))
  }, []);
  
  return (
    <div>
        <Cards cards={cards}   targetRef ={targetRef}/>
    </div>
  )
}

export default ListCard
