import React from 'react';
import Categorie from '../../components/Categories/categorie';
import ListCard from '../../components/PizzaCard/listCard';
import OftenOrder from '../../components/OftenOrder/ListMiniCard';
import Footer from './Footer/Footer';
import { Route, Routes } from 'react-router-dom';

// export const Context = React.createContext('')

function MainPage() {
  const [cards, setCards] = React.useState([]);
  const [search, setSearch] = React.useState('');
  const [OftOrdCard, setOftOrdCard] = React.useState([]);
  const [isLoading, setIsLoading] = React.useState(true)

  const [addBtn, setAddBtn] = React.useState(1);
  const AddBtn = () => { setAddBtn(addBtn + 1) }

  const targetRef = React.useRef(null);
  const scrollToRef = () => {
    targetRef.current.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    // <Context.Provider value={{ search, setSearch, targetRef, cards, setCards, OftOrdCard, setOftOrdCard, AddBtn, addBtn, scrollToRef, isLoading, setIsLoading}}>
       <div>
          <OftenOrder />
          <Categorie />
          <Routes>
            <Route path="PizzaCard" element={<ListCard />} />
          </Routes>
          <Footer />
       </div>
    // </Context.Provider>
  );
}

export default MainPage;
