import React from 'react';
import Header from '../Header/Header';
import Categorie from '../../components/Categories/categorie';
import ListCard from '../../components/PizzaCard/listCard';
import OftenOrder from '../../components/OftenOrder/ListMiniCard';
import Footer from '../Footer/Footer';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';

export const Context = React.createContext('')

function MainPage() {
  const [cards, setCards] = React.useState([]);
  const [search, setSearch] = React.useState('');
  const [OftOrdCard, setOftOrdCard] = React.useState([]);
  
  const [addBtn, setaddBtn] = React.useState('');
  const AddBtn = () => {setaddBtn(addBtn+1)} 
  const EventAddBtn = () => {setaddBtn()} 
  
  const targetRef = React.useRef(null);
  const scrollToRef = () => {
    targetRef.current.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <Context.Provider value={{ search, setSearch, targetRef, cards, setCards, OftOrdCard, setOftOrdCard, AddBtn}}>
      <Router>
        <Header />
        <Categorie scrollToRef={scrollToRef} />
        <OftenOrder />
        <Routes>
          <Route path="/" element={<ListCard />} />
        </Routes>
        <Footer />
      </Router>
    </Context.Provider>
  );
}

export default MainPage;
