import { createSlice } from "@reduxjs/toolkit";

export const cardSlice = createSlice({
  name: 'card',
  initialState: { 
   PizzaCard: []
  },
  reducers: {
    setPizza(state, action) {
        state.PizzaCard.push(action.payload)}
    },
})

export const { addItem } = cardSlice.actions;
export default cardSlice.reducer;
